function load(lrcUrl, audioUrl) {
  fetch(lrcUrl)
    .then(response => response.text())
    .then(lrcText => {
      localStorage.ON_T_CI_index = 1;
      localStorage.ON_T_CI_L = 0;
      localStorage.ON_T_CI_S = 2000;
