function changeTimeFormat(e) {
            e = e.replace("[", "");
            e = e.replace("]", "");
            var time = e.split('.')[0].split(':'), s;

            if (time.length == 3) {
                s = time[0] * 3600 + time[1] * 60 + time[2] * 100 / 100;
            } else if (time.length == 2) {
                s = time[0] * 60 + time[1] * 100 / 100;
            }
            s += e.split('.')[1] / 100;
            return s;
        }
